int stup(int n, int m, int k) {
    const int N = 21;
    int D[3][N][N][N];
    memset(D, 0, sizeof(D));
    for (int s = 0; s < 3 * N; s++) {
        for (int x = 0; x <= min(s, N - 1); x++) {
            for (int y = 0; x + y <= s && y <= N - 1; y++) {
                int z = s - x - y;
                if (z < 0 || z > N - 1)
                    continue;
                for (int w = 0; w < 3; w++) {
                    int g[] = {x, y, z};
                    if (g[w] == 0) {
                        if (w == 0)
                            D[w][x][y][z] = pw3[y + z];
                        else
                            D[w][x][y][z] = 0;
                    } else {
                        g[w]--;
                        D[w][x][y][z] = (1ll * D[0][g[0]][g[1]][g[2]] + D[1][g[0]][g[1]][g[2]] + D[2][g[0]][g[1]][g[2]]) % MOD;
                    }
                }
            }
        }
    }
    return D[0][n][m][k];
}

int powmod(int x, int b) {
    int res = 1;
    while (b > 0) {
        if (b & 1)
            res = 1ll * res * x % MOD;
        x = 1ll * x * x % MOD;
        b >>= 1;
    }
    return res;
}

inline int inv(int x) {
