inline void init(int n) {
	fac[0] = pwr[0] = 1;
	for(int i = 1; i <= n; i ++ ) fac[i] = (ll)i*fac[i-1]%p, pwr[i] = pwr[i-1]*(ll)3%p;
	inv[n] = qp(fac[n], p-2);
	for(int i = n; i >= 1; i -- ) inv[i-1] = (ll)i*inv[i]%p;
}
inline ll c(int n, int m) {
	if(n < m) return 0;
	return (ll)fac[n]*inv[m]%p*inv[n-m]%p;
}
