    } else {
        return ans;
    }
}

ll comb(ll k, ll n) {
    if(k < 0 || n < 0) {return 0;}
    if(n - k < 0) {return 0;}
    return (((perm[n] * inv[n - k]) % mod) * inv[k]) % mod;
}

