    perm[0] = inv[0] = 1;
    for(ll i = 1; i < MAX_N; i ++) {
        perm[i] = (perm[i - 1] * i) % mod;
        inv[i] = (inv[i - 1] * fpow(i, mod - 2)) % mod;
    }
