        while(nwl < rangel) {
            sum = (sum + comb(rangel - 1, ij)) % mod;
            rangel --;
        }
        while(nwl > rangel) {
            sum = (sum - comb(rangel, ij)) % mod;
            rangel ++;
        }
        while(nwr > ranger) {
            sum = (sum + comb(ranger + 1, ij)) % mod;
            ranger ++;
