	for(int i=1;i<=n;i++)fac[i]=(LL)fac[i-1]*i%MOD;
	for(int i=1;i<=n;i++)inv[i]=qpow(fac[i],MOD-2,MOD);
}
int Comb(int n,int m){
	if(n-m>=0&&m>=0) return 1ll*fac[n]*inv[n-m]%MOD*inv[m]%MOD;
	else return 0;
}

