  n--;

  LL j=1,ans=0;
  for(int i=0;i<=m+k;i++){
    ans=(ans+(LL)Comb(n+i,n)*pow3[m+k-i]%MOD*j)%MOD;
    if(i<k)
        j=j*2ll%MOD;
    else if(i<m)
        j=(j*2ll-Comb(i,k))%MOD;
    else    
        j=(j*2ll-Comb(i,k)-Comb(i,m))%MOD;
  }
  ans=(ans+MOD)%MOD;
  cout<<ans<<endl;
}

