	ll l=0,r=0;
	for(int L=0;L<=m+k-2;L++)
	{
		ll now=C(L+n-2,n-2)*rt[m+k-L-2]%MOD;
		if(L-k>0)
		{
			l*=2LL;
			l%=MOD;
			l+=C(L-1,k-1);
