const LL MOD = 1000000007ll;

LL m_pow(LL a,LL n)
{
    if(n == 0)return 1;
    LL half = m_pow(a,n/2);
    half *= half;
    half %= MOD;
    return (half * (n % 2 ? a : 1)) % MOD;
}
vector<LL>fact;
LL comb(LL n,LL r)
{
    LL shi = fact[n];
    LL bo1 = m_pow(fact[r],MOD - 2);
    LL bo2 = m_pow(fact[n - r],MOD - 2);
    shi *= bo1;
    shi %= MOD;
    shi *= bo2;
    shi %= MOD;
    return shi;
}

