void mul(int n, LL *a, LL *b, LL *c) {
  if (n < 16) {
    memset(c, 0, sizeof(LL) * (n + n - 1));
    REP(i, n) REP(j, n) {
      c[i + j] = (c[i + j] + a[i] * b[j]);
