    bb[i] = (b[i] + b[i + m]); if (bb[i] >= mod) bb[i] -= mod;
  }
  mul(m, a, b, x);
  mul(o, a + m, b + m, y);
  mul(o, aa, bb, z);
  memcpy(c, x, sizeof(LL) * (m * 2 - 1));
  memcpy(c + m + m, y, sizeof(LL) * (o * 2 - 1));
  REP(i, o * 2 - 1) {
    z[i] -= x[i] + y[i];
    if (z[i] < 0) z[i] += mod;
    if (z[i] < 0) z[i] += mod;
    c[i + m] += z[i];
