				else
					ret += dp[p][m][k];
				if (ret >= MOD)
					ret -= MOD;
				if (m)
				 ret += dp[c][m - 1][k];
				if (ret >= MOD)
					ret -= MOD;
				if (k)
					ret += dp[c][m][k - 1];
				if (ret >= MOD)
					ret -= MOD;
			}
	}
	cout << dp[(N - 1) & 1][M][K] << endl;
	return 0;
}

