      if (i > M) dp[i] -= C(i - 1, M);
      if (i > K) dp[i] -= C(i - 1, K);
   }
   Mint ans = 0;
   for (int i = 0; i <= M + K; ++i) {
      ans += C(N - 1 + i, i) * dp[i] * pow3[M + K - i];
   }
