    constexpr modint operator--(int) { modint m = *this; --(*this); return m; }
    friend constexpr modint operator+(long long x, const modint& y) { return modint(x) + y; }
    friend constexpr modint operator*(long long x, const modint& y) { return modint(x) * y; }
    friend constexpr modint operator-(long long x, const modint& y) { return modint(x) - y; }
    friend constexpr modint operator/(long long x, const modint& y) { return modint(x) / y; }
    friend ostream& operator<<(ostream& os, const modint& m) { return os << m.val; }
    friend istream& operator>>(istream& is, modint& m) { ll val; is >> val; m = modint(val); return is; }
};
constexpr int M = 1'000'000'007;
using mint = modint<M, M - 1>;
mint operator""_m(unsigned long long int x) { return mint(ll(x)); }
template <typename T = mint> T fact(int n) {
    static vector<T> fac = {1};
    while(fac.size() <= n) {
        fac.push_back(fac.back() * int(fac.size()));
    }
    return fac[n];
}
template <typename T = mint> T fact_inv(int n) {
    static vector<T> inv_fac = {1};
    while(inv_fac.size() <= n) {
        inv_fac.push_back(inv_fac.back() / int(inv_fac.size()));
    }
    return inv_fac[n];
}
const double PI = acos(-1);
complex<long double> w(int len, bool inverse) {
    return polar<long double>(1, (inverse ? -2 : 2) * (PI / len));
}
constexpr int lg(int x) { return 31 - __builtin_clz(x); }
template <typename T> vector<T>& pad(vector<T>& a, int min_size) {
    a.resize(2 << lg(min_size - 1));
    return a;
}
int bit_reverse(int i, int n) {
    int ans = 0;
    for(int j = 0; j < lg(n); j++) {
        ans |= i & (1 << j) ? 1 << (lg(n) - 1 - j) : 0;
    }
    return ans;
}
template <typename T> vector<T>& fft(vector<T>& a, bool inverse = false) {
    int n = a.size();
    for(int i = 0; i < n; i++) {
        if(i < bit_reverse(i, n)) {
            swap(a[i], a[bit_reverse(i, n)]);
        }
    }
    for(int len = 2; len <= n; len <<= 1) {
        T W = w(len, inverse);
        for(int i = 0; i < n; i += len) {
            T root = 1;
            for(int j = i; j < i + (len >> 1); j++) {
                T u = a[j], t = root * a[j + (len >> 1)];
                a[j] = u + t;
                a[j + (len >> 1)] = u - t;
                root *= W;
            }
        }
    }
    if(inverse) {
        transform(a.begin(), a.end(), a.begin(), [n](T& ai) { return ai / T(n); });
    }
    return a;
}
template <typename T> vector<T>& operator*=(vector<T>& a, vector<T>& b) {
    fft(pad(a, a.size())); if(addressof(a) != addressof(b)) fft(pad(b, b.size()));
    assert(a.size() == b.size());
    transform(a.begin(), a.end(), b.begin(), a.begin(), multiplies<T>());
    fft(a, true); if(addressof(a) != addressof(b)) fft(b, true);
    return a;
}
template <typename T> vector<T> operator*(vector<T> a, vector<T>& b) { return a *= b; }
template <int C = int(sqrt(mint::MOD) + 0.5)> vector<mint> operator*=(vector<mint>& a, vector<mint>& b) {
    vector<complex<long double>> a1(a.size()), a2(a.size()), b1(b.size()), b2(b.size());
    transform(a.begin(), a.end(), a1.begin(), [](mint& ai) { return ai.val % C; });
    transform(a.begin(), a.end(), a2.begin(), [](mint& ai) { return ai.val / C; });
    transform(b.begin(), b.end(), b1.begin(), [](mint& bi) { return bi.val % C; });
    transform(b.begin(), b.end(), b2.begin(), [](mint& bi) { return bi.val / C; });
    vector<complex<long double>> a1b1 = b1 * a1, &a2b1 = b1 *= a2, &a1b2 = a1 *= b2, &a2b2 = a2 *= b2;
    for(int i = 0; i < a.size(); i++) {
        a[i] = mint(a1b1[i]) + mint(a1b2[i]) * C + mint(a2b1[i]) * C + mint(a2b2[i]) * C * C;
    }
    return a;
}
template <int C = int(sqrt(mint::MOD) + 0.5)> vector<mint> operator*(vector<mint> a, vector<mint>& b) { return a *= b; }
int main() {
