const int N = 1e6 + 10, mod = 1e9 + 7;

int fact[N], inv[N];

int comb(int n, int k)
{
	if(k > n || k < 0) return 0;
	ll res = fact[n];
	res *= inv[k], res %= mod;
	res *= inv[n - k], res %= mod;
	return res;
}

ll power(int a, int b)
{
	if(b == 0) return 1;
	ll res = power(a, b/2);
	res *= res, res %= mod;
	if(b & 1) res *= a, res %= mod;
	return res;
}

int main()
{
