template<uint_fast64_t Modulus = MOD>
struct Modint {
  using u64 = uint_fast64_t;
  u64 a;

  constexpr Modint(const u64 x = 0) noexcept : a(x % Modulus) {}

  constexpr Modint operator+(const Modint rhs) const noexcept {
    return Modint(*this) += rhs;
  }
  constexpr Modint operator-(const Modint rhs) const noexcept {
    return Modint(*this) -= rhs;
  }
  constexpr Modint operator*(const Modint rhs) const noexcept {
    return Modint(*this) *= rhs;
  }
  constexpr Modint operator/(const Modint rhs) const noexcept {
    return Modint(*this) /= rhs;
  }

  constexpr Modint &operator+=(const Modint rhs) noexcept {
    a += rhs.a;
    if (a >= Modulus) a -= Modulus;
    return *this;
  }
  constexpr Modint &operator-=(const Modint rhs) noexcept {
    if (a < rhs.a) a += Modulus;
    a -= rhs.a;
    return *this;
  }
  constexpr Modint &operator*=(const Modint rhs) noexcept {
    a = a * rhs.a % Modulus;
    return *this;
  }
  constexpr Modint &operator/=(Modint rhs) noexcept {
    u64 exp = Modulus - 2;
    while (exp) {
      if (exp & 1) *this *= rhs;
      rhs *= rhs;
      exp >>= 1;
    }
    return *this;
  }

  explicit operator bool() const {
    return a;
  }

  friend ostream &operator<<(ostream &os, const Modint &m) {
    return os << m.a;
  }
};

using mint = Modint<>;

template<typename T>
struct ModCombination {
  vector<T> fac, finv, inv;

  ModCombination(int MAX) : fac(MAX + 1), finv(MAX + 1), inv(MAX + 1) {
    fac[0] = finv[MAX] = inv[0] = 1;
    for (int i=1; i<=MAX; ++i) fac[i] = fac[i-1] * i;
    finv[MAX] /= fac[MAX];
    for (int i=MAX-1; i>=0; --i) finv[i] = finv[i+1] * (i+1);
    for (int i=1; i<=MAX; ++i) inv[i] = finv[i] * fac[i-1];
  }

  T P(int n, int r) const {
    if (n < r || n < 0 || r < 0) return 0;
    return fac[n] * finv[n-r];
  }

  T C(int n, int r) const {
    if (n < r || n < 0 || r < 0) return 0;
    return fac[n] * finv[r] * finv[n-r];
  }

  T H(int n, int r) const {
    if (n < 0 || r < 0) return 0;
    return C(n + r - 1, r);
  }
};

void solve() {
  int n, m, k; cin >> n >> m >> k;

  ModCombination<mint> comb(n+m+k);

  mint ans = 0;
  mint div = mint(1) / 3;
  mint sum = 1;
  mint pow3 = modpow(3, m+k);

  repeq(i,m+k) {
    ans += comb.C(n-1 + i, i) * sum * pow3;
    sum *= 2;
    if (i >= m) sum -= comb.C(i, m);
    if (i >= k) sum -= comb.C(i, k);
    pow3 *= div;
  }

  cout << ans << endl;
}


