inline void dft(int n, int kier)
{
    int n2=n-1;
    int s=0;
    int p;
    int g;

    for (int i=2; i<=n; i<<=1)
    {

        for (int j=0; j<n; j++)
        {
            dfts[j]=dftn[j];
            dftn[j]=0;
        }

        p=n/i;

        if (kier)
        {
            g=0;
            for (int j=0; j<n; j++)
            {
                dftn[j]=dfts[(2*(j-s)+s)&n2]+omega[g]*dfts[(2*(j-s)+p+s)&n2];
                s++;
                if (s==p)
                {
                    g=j+1;
                    s=0;
                }
            }
        }
        else
        {
            g=n;
            for (int j=0; j<n; j++)
            {
                dftn[j]=dfts[(2*(j-s)+s)&n2]+omega[g]*dfts[(2*(j-s)+p+s)&n2];
                s++;
                if (s==p)
                {
                    g=n-j-1;
                    s=0;
                }
            }
        }
    }
}

void licz_omegi(int n1)
{
    double kat=2.0*PI/n1;
    int n2=n1-1;
    int dod=3*n1/4;

    for (int i=0; i<=n1; i++)
    cosi[i]=cos(kat*i);
    for (int i=0; i<=n1; i++)
    omega[i]=complex <double> (cosi[i], cosi[(i+dod)&n2]);
}

