	REP(i, 2, 1000000){
		inv[i] = (LL)inv[i - 1] * inv[i] % P;
	}
	int ff = 1;
	int ans = 0;
	REP(x, 0, m + k){
		int l = max(0, x - k);
		int r = min(x, m);
		int sum = 0;

		if(r == x){
			sum = ff;
		}else{
			if(x == m + 1){
				g[x] = ff;
				renew(g[x], -1);
			}
			g[x + 1] = (0LL + g[x] + g[x] + P - C(x, m)) % P;
			sum = g[x];
		}
		if(l > 0){
			if(l == 1){
				f[x] = 1;
			}
			f[x + 1] = (0LL + f[x] + f[x] + C(x, k)) % P;
			renew(sum, -f[x]);
		}

		renew(ans, (LL)sum * C(n - 1 + x, x) % P * Pow(3, m + k - x, P) % P);
		ff = (ff << 1) % P;
	}
	printf("%d\n", ans);
	return 0;
}

