typedef long long int lli;

const int MOD = 1e9+7;
const int facHausSize = 900005;
lli facHaus[facHausSize];

lli powe(int a, int b) {
	if(b < 2) return b? a:1;
	else {
		lli base = powe(a, b/2);
		lli ans = base*base;
		ans %= MOD;
		if(b%2) {
			ans *= a;
			ans %= MOD;
		}
		return ans;
	}
}

lli inv(int a) {
	return powe(a, MOD-2);
}

lli choos(int a, int b) {
	if(a < b || b < 0) return 0;
	lli ans = facHaus[a];
	ans %= MOD;
	ans *= inv(facHaus[b]);
	ans %= MOD;
	ans *= inv(facHaus[a-b]);
	ans %= MOD;
	return ans;
}

int N, M, K;

int main() {
