	}

	cin >> N >> M >> K;

	//ans = summation of [(N-1+m+k)C(m+k)][(m+k)Cm]3^(M+K-m-n) from m = 0 to M and from k = 0 to K
	lli termOne = 1;
	lli termTwo = 1;
	lli termThree = powe(3, M+K);
	lli base = termOne*termTwo*termThree;
	lli ans = base;
	for(int summe = 1; summe <= M+K; summe++) {
		termOne *= (N-1+summe);
		termOne %= MOD;
		termOne *= inv(summe);
		termOne %= MOD;

		termTwo *= 2;
		if(summe > M) {
			termTwo -= choos(summe-1, M);
		}
		if(summe > K) {
			termTwo -= choos(summe-1, K);
		}
		termTwo += 2*MOD;
		termTwo %= MOD;

		termThree = powe(3, M+K-summe);

		base = termOne*termTwo;
		base %= MOD;
		base *= termThree;
		base %= MOD;

		ans += base;
		ans %= MOD;
	}

	cout << ans << endl;

	return 0;
}

