set<pii> :: iterator it;

pii find_near(ll n,ll r){
    pii ans=mp(0,1);
    it=st[n].lower_bound(mp(r,0));
    if(it!=st[n].end()){
        if(abs((*it).F-r)<abs(r-ans.F)){
            ans=*it;
        }
    }
    if(it!=st[n].begin()){
        it--;
        if(abs((*it).F-r)<abs(r-ans.F)){
            ans=*it;
        }
    }
    return ans;
}
ll find_pre(ll n,ll r){
    if(r==n){
        ll res=poww(2,n);
        st[n].insert(mp(r,res));
        return res;
    }
    pii e=find_near(n,r);
    if(abs(e.F-r)<5){
        while(e.F<r){
            e.F++;
            e.S=(e.S+ent(n,e.F))%mod;
        }
        while(e.F>r){
            e.S=(e.S-ent(n,e.F)+mod)%mod;
            e.F--;
        }
        st[n].insert(e);
        return e.S;
    }
    ll res=(find_pre(n-1,r)*2-ent(n-1,r)+mod)%mod;
    st[n].insert(mp(r,res));
    return res;
}
