const int MX = 910000;
ll inv[MX], f[MX], invf[MX], pw[MX];
void calc(){
	pw[0] = 1;
	rep(i, MX - 1) pw[i + 1] = pw[i] * 3 % mod;
	inv[0] = inv[1] = f[0] = f[1] = invf[0] = invf[1] = 1;
	for (int i = 2; i < MX; i++){
		inv[i] = mod - mod / i * inv[mod % i] % mod;
		f[i] = f[i - 1] * i % mod;
		invf[i] = invf[i - 1] * inv[i] % mod;
	}
}
inline ll C(ll n, ll k){
	if(k < 0 || k > n) return 0;
	assert(n < MX && k < MX);
	return f[n] * invf[k] % mod * invf[n - k] % mod;
}
