		if(l > L) sum += mod - C(i, L);
		if(r > R) sum += C(i, r);

		assert(l == L || L + 1 == l); assert(r == R || R + 1 == r);
		//dbg(l, r, L, R, sum, s); assert(s % mod == sum % mod);

		L = l; R = r;
