namespace Karatsuba
{
	#pragma GCC optimize("Ofast,unroll-loops")
	#pragma GCC target("avx,avx2,fma")

	template<int n, typename T>
	void mult(const T* __restrict A, const T* __restrict B, T* __restrict C)
	{
		if (n <= 64)
		{
			FOR(i, 0, n) FOR(j, 0, n) C[i + j] += A[i] * B[j];
			return;
		}

		const int m = n >> 1;
		mult<m>(A + 0, B + 0, C);
		mult<m>(A + m, B + m, C + n);

		alignas(64) T a[n], b[n], e[n] = {};
		FOR(i, 0, m) 
		{
			a[i] = A[i] + A[i + m];
			b[i] = B[i] + B[i + m];
		}

		mult<m>(a, b, e);
		FOR(i, 0, m)
		{
			const T tut = C[i + m];
			C[i + m] += e[i] - C[i] - C[i + m * 2];
			C[i + m * 2] += e[i + m] - tut - C[i + m * 3];
		}
	}
};

