	for(int i = 2 ; i < MAXN ; ++ i)
		ifac[i] = MOD - 1ll * ifac[MOD % i] * (MOD / i) % MOD;
	for(int i = 2 ; i < MAXN ; ++ i)
		ifac[i] = 1ll * ifac[i - 1] * ifac[i] % MOD;
	f3[0] = 1;
	for(int i = 1 ; i < MAXN ; ++ i)
		f3[i] = 1ll * f3[i - 1] * 3 % MOD;
}

