    for(int i = 1; i <= M + K; i++)
        for(int j = max(0, i - K); j <= M && j <= i; j++)
            cnt[i] = (cnt[i] + C(i, j)) % mod;

    int ans = 0;
    for(int i = N; i <= N + M + K; i++)
    {
        int sol = (1LL * C(i - 1, N - 1) * cnt[i - N]) % mod;
        sol = (1LL * sol * p3[N + M + K - i]) % mod;
        ans = (ans + sol) % mod;
    }

    printf("%d\n", ans);

    return 0;
}

