ll fact[FMAX];
ll ifact[FMAX];

ll modpow(ll a,ll b,ll c){
  ll r = 1;
  while(b){
    if(b&1)r=r*a%c;
    a=a*a%c;
    b>>=1;
  }
  return r;
}
ll modinv(ll a,ll c){
  return modpow(a,c-2,c);
}

int main(){
  scanf("%d%d%d",&n,&m,&k);
  // fact init
  fact[0] = 1;
  FOR(i,1,FMAX)fact[i]=fact[i-1]*i%MOD;
  ifact[FMAX-1]=modinv(fact[FMAX-1],MOD);
  FORR(i,0,FMAX-1)ifact[i]=ifact[i+1]*(i+1)%MOD;
  // a == n-1 && b <= m && c <= k
  ll ans = 0;
  int a = n-1;
  ll bipow = 1;
  ll subsum = 0;
  ll tris = modpow(3,m+k,MOD);
  ll invtri = modinv(3,MOD);
  ll factabc = (a==0?1:fact[a-1]);
  int befbeg,befend;
  FOR(s,0,m+k+1){
    // c = s-b
    // 0<=c<=k
    // 0<=b<=m

    // s-b>=0 -> b<=s
    // s-b<=k -> b>=s-k
    int abc = a+s;
    if(abc!=0){
      factabc = factabc*abc%MOD;
