    ll unit = factabc*ifact[a]%MOD*ifact[s]%MOD*tris%MOD;
    tris = tris*invtri%MOD;

    int beg = max(s-k,0);
    int end = min(s,m);
    if(s>0){
      bipow += bipow;
      subsum *= 2;
      if(befbeg+1==beg){
        subsum += fact[s-1]*ifact[befbeg]%MOD*ifact[s-1-befbeg]%MOD;
      }
      if(befend==end){
        subsum += fact[s-1]*ifact[befend]%MOD*ifact[s-1-befend]%MOD;
      }
      if(bipow>=MOD)bipow-=MOD;
      subsum %= MOD;
    }
    befbeg = beg;
    befend = end;
    ans += unit*(bipow+MOD-subsum)%MOD;
    if(ans>=MOD)ans-=MOD;
  }
  printf("%lld\n",ans);
  return 0;
}

