int fac[N],inv[N];
void init(int n){
	fac[0]=inv[0]=1;
	for(int i=1;i<=n;i++)fac[i]=(LL)fac[i-1]*i%MOD;
	for(int i=1;i<=n;i++)inv[i]=qpow(fac[i],MOD-2,MOD);
}
int Comb(int n,int m){
	if(n-m>=0&&m>=0) return 1ll*fac[n]*inv[n-m]%MOD*inv[m]%MOD;
	else return 0;
}

int pow3[N];
void Solve(){
  pow3[0]=1;
  rep(i,1,N-1)pow3[i]=(LL)pow3[i-1]*3%MOD;
  init(1e6+5);

  int n,m,k;
  cin>>n>>m>>k;
  n--;

  LL j=1,ans=0;
  for(int i=0;i<=m+k;i++){
    ans=(ans+(LL)Comb(n+i,n)*pow3[m+k-i]%MOD*j)%MOD;
    if(i<k)
        j=j*2ll%MOD;
    else if(i<m)
        j=(j*2ll-Comb(i,k))%MOD;
    else    
        j=(j*2ll-Comb(i,k)-Comb(i,m))%MOD;
  }
  ans=(ans+MOD)%MOD;
  cout<<ans<<endl;
}

