int N,M,K; lld ans;
lld fact[900002],inv[900002],three[900002],two[900002];

lld mul(lld x,lld y){
	if(y == 0) return 1;
	lld tmp = mul(x,y/2);
	tmp *= tmp; tmp %= Mod;
	if(y%2 == 1){
		tmp *= x;
		tmp %= Mod;
	}
	return tmp;
}
lld comb(lld x,lld y){
	return (fact[x]*inv[x-y]%Mod)*inv[y]%Mod;
}

int main(){
	scanf("%d %d %d",&N,&M,&K);
	fact[0] = inv[0] = three[0] = two[0] = 1;
	for(int i=1; i<=900000; i++){
		fact[i] = (fact[i-1]*i)%Mod;
		inv[i] = mul(fact[i],Mod-2);
		three[i] = (three[i-1]*3)%Mod;
		two[i] = (two[i-1]*2)%Mod;
