  return power(x, md - 2);
}

const int N = 1234567;

int fact[N], invfact[N];

inline int C(int n, int k) {
  if (k < 0 || k > n) return 0;
  return mul(fact[n], mul(invfact[k], invfact[n - k]));
}

int main() {
