    int what = power(2, sum);
    if (sum > b) {
      add(rb, rb);
      add(rb, C(sum - 1, b));
      add(what, md - rb);
    }
    if (sum > c) {
      add(rc, rc);
      add(rc, C(sum - 1, c));
      add(what, md - rc);
    }
    what = mul(what, invfact[sum]);
    add(ans, mul(coeff, what));
