  dp();
  ll ans = 0;
  for(int i = n; i <= n + m + k; i ++) {
    ll val = f[i - n] * C(i - 1, n - 1) % ha;
    val *= pow_mod(3, n + m + k - i);
    ans = (ans + val) % ha;
  }
  printf("%lld\n", ans);
  return 0;
}

