	ll ans = 0;
	ll now = 1;
	int l = 0, r = 0;
	for (int i = N; i <= N + M + K; i++) {
		ll x = mod_pow(3, N + M + K - i);
		(x *= comb(i - 1, N - 1)) %= MOD;
		(x *= now) %= MOD;
		(ans += x) %= MOD;
		(now *= 2) %= MOD;
		if (i >= N + K) {
			(now += MOD - comb(i - N, l)) %= MOD;
			l++;
		}
		if (i >= N + M) {
			(now += MOD - comb(i - N, r)) %= MOD;
		}
		else {
			r++;
		}
	}
