  // = \sum_{d = 0}^{m + k} fact(d + n - 1) / fact(n - 1) / fact(d) \sum_{b = max(0, d - k)}^{min(d, m)} C(d, b) 3^{m + k - d}
  ll sum = 0;
  ll cur = 1;
  REP(d, 0, m + k + 1) {
    ll tmp = fact[d + n - 1];
    tmp *= invmod(fact[n - 1]);
