void COMinit(){
	fac[0] = fac[1] = 1;
	finv[0] = finv[1] = 1;
	inv[1] = 1;
	for(int i = 2; i < 600005; i++){
		fac[i] = fac[i - 1] * i % MOD;
		inv[i] = MOD - inv[MOD % i] * (MOD / i) % MOD;
		finv[i] = finv[i - 1] * inv[i] % MOD;
	}
}
long long COM(int n, int k){
	if(n < k) return 0;
	if(n < 0 || k < 0) return 0;
	return fac[n] * (finv[k] * finv[n - k] % MOD) % MOD;
}
int main(){
	int N, M, K; cin >> N >> M >> K;
	COMinit(); 
	long long pow3[600010] = {}; 
	pow3[0] = 1; 
	for(int i = 1; i < 600005; i++) pow3[i] = pow3[i-1] * 3 % MOD; 
	long long A[600010] = {}; 
	long long B[600010] = {}; 
	long long C[600010] = {};
	A[0] = 1; 
	for(int i = 1; i <= M+K; i++){
		A[i] = ((A[i-1] * inv[i]) % MOD) * (N-1+i) % MOD; 
	}
	B[0] = 1; 
	for(int i = 1; i <= M+K; i++){
		B[i] = B[i-1] * 2 % MOD;
		long long mn = 0;  
		if(i > M){
			long long tmp = (fac[i-1] * finv[i-1-M]) % MOD; 
			tmp *= finv[M]; tmp %= MOD; mn += tmp;
		}
		if(i > K){
			long long tmp = (fac[i-1] * finv[i-1-K]) % MOD; 
			tmp *= finv[K]; tmp %= MOD; mn += tmp;
		}
		B[i] += MOD * 2 - mn; B[i] %= MOD; 
	}
	long long ans = 0; 
	for(int i = 0; i <= M+K; i++){
		ans += (B[i] * pow3[M+K-i] % MOD) * A[i] % MOD; 
		ans %= MOD;
	}
	cout << ans << "\n"; 
}

