constexpr long long int MOD = 1000000007LL;
long long int power(const long long int base, const int exp) {
	switch (exp) {
	case 0: return 1LL;
	case 1: return base % MOD;
	default: return power(base * base % MOD, exp >> 1) * power(base, exp & 1) % MOD;
	}
}
int main() {
	int n, m, k; std::cin >> n >> m >> k;
	std::vector<long long int> factorial(n + m + k + 1, 1), div(n + m + k + 1, 1), inverse(n + m + k + 1, 1);
	for (auto i = 2; i < factorial.size(); ++i) {
		factorial[i] = i * factorial[i - 1] % MOD;
		div[i] = (MOD - MOD / i) * div[MOD % i] % MOD;
		inverse[i] = div[i] * inverse[i - 1] % MOD;
	}
	const auto combination = [&factorial, &inverse](const int n, const int r) {return factorial[n] * inverse[n - r] % MOD * inverse[r] % MOD; };
	long long int result{ power(3, m + k) };
	long long int prev{ 1 };
	for (auto len = n + 1; len <= n + m + k; ++len) {
		const auto rest = len - n;
		const auto a_pattern = combination(len - 1, n - 1);
		const auto min = std::max(rest - k, 0);
		const auto max = std::min(rest, m);
		prev <<= 1;
		if (min > 0) {
			prev += MOD - combination(rest - 1, min - 1);
		}
		if (rest > m) {
			prev += MOD - combination(rest - 1, m);
		}
		prev %= MOD;
		result = (result + a_pattern * prev % MOD * power(3, n + m + k - len)) % MOD;
	}
	std::cout << result << std::endl;
}

#include <cstdio>
