void incL() {
    L++;
    res = sum(res, cnk(R, L));
}
void decL() {
    res = sub(res, cnk(R, L));
    L--;
}
void incR() {
    res = sum(res, res);
    res = sub(res, cnk(R, L));
    R++;
}
const int inv2 = (mod + 1) / 2;
void decR() {
    R--;
    res = sum(res, cnk(R, L));
    res = mult(res, inv2);
}
