// a^-1 mod p
ll inv(ll a,ll p){
  return ( a == 1 ? 1 : (1 - p*inv(p%a,a)) / a + p );
}

ll pow3(ll x) {
  if (x == 0) return 1;
  if ((x%2)==1) return 3*pow3(x-1)%MOD;
  ll h=pow3(x/2);
  return h*h%MOD;
}

int main()
{
  ll n,m,k;
  cin>>n>>m>>k;
  vecll fact(3001);
  vecll factinv(3001);
  fact[0] = 1;
  factinv[0] = 1;
  for(int i:irange(0,3000)) {
    fact[i+1] = fact[i] * (i+1) % MOD;
    factinv[i+1] = inv(fact[i+1], MOD);
  }
  ll ans = 0;
  ll l=n+m+k;
  ll rsum = 1;
  for(ll i:irange(n,l+1)) {
    ll r = i-n;
    ll comb = fact[i-1] * factinv[n-1] % MOD;
    comb = comb * factinv[r] % MOD;
    ll pw = pow3(l-i);
    ll mul = comb * pw % MOD * rsum % MOD;
    ans += mul;
    ans %= MOD;
    rsum *= 2;
    rsum %= MOD;
    if (r >= k) {
      ll comb2 = fact[r] * factinv[r-k] % MOD;
      comb2 = comb2 * factinv[k] % MOD;
      rsum += MOD - comb2;
      rsum %= MOD;
    }
    if (r >= m) {
      ll comb2 = fact[r] * factinv[r-m] % MOD;
      comb2 = comb2 * factinv[m] % MOD;
      rsum += MOD - comb2;
      rsum %= MOD;
    }
  }
  cout<<ans<<endl;
  return 0;
}

