	clear(A,ln);clear(B,ln);clear(C,ln);clear(D,ln);clear(A1,ln);clear(B1,ln);clear(C1,ln);
	for(int i=0;i<n;i++)A[i]=Complex(a[i]/M,0),B[i]=Complex(a[i]%M,0);
	for(int i=0;i<m;i++)C[i]=Complex(b[i]/M,0),D[i]=Complex(b[i]%M,0);
	FFT(A,ln,1);FFT(B,ln,1);FFT(C,ln,1);FFT(D,ln,1);
	for(int i=0;i<ln;i++)A1[i]=A[i]*C[i],B1[i]=B[i]*C[i]+A[i]*D[i],C1[i]=B[i]*D[i];
	FFT(A1,ln,-1);FFT(B1,ln,-1);FFT(C1,ln,-1);
	for(int i=0;i<n+m-1;i++)A1[i].r=(LL)(A1[i].r+0.5),B1[i].r=(LL)(B1[i].r+0.5),C1[i].r=(LL)(C1[i].r+0.5);
