    for(ll j=0; j<=i-N; ++j){
      if(j<=M && i-N-j<=K) ccnt+=COM(i-N,j);
      ccnt%=MOD;
    }
    cnt*=ccnt;
    cnt%=MOD;

    ans+=cnt;
    ans%=MOD;
  }

