ll memo[1000][1000][1000][3];

ll solve(ll n, ll m, ll k, char turn){
    if(memo[n][m][k][turn-'a'] != -1)   return memo[n][m][k][turn-'a'];
    ll sum = 0;
    if(turn == type[0]){
        if(n == 0)  return pow(3, m+k);
        sum += solve(n-1, m, k, 'a');
        sum += solve(n-1, m, k, 'b');
        sum += solve(n-1, m, k, 'c');
    }
    if(turn == type[1]){
        if(m == 0)  return 0;
        sum += solve(n, m-1, k, 'a');
        sum += solve(n, m-1, k, 'b');
        sum += solve(n, m-1, k, 'c');
