    for (int i = 0; i < M + K; i++) { dp[i + 1] = dp[i] * 2 - mod.comb(i, M) - mod.comb(i, i - K); }
    mint ans = 0;
    for (int s = 0; s <= M + K; s++) { ans += P[M + K - s] * mod.comb(N + s - 1, N - 1) * dp[s]; }
    std::cout << ans << std::endl;
    return 0;
}

