        if (b + c < n) continue;
        int cur = 0;
        for (int x = n - c; x <= b; ++x) {
            cur = (cur + C(len - a, x)) % mod;
        }
        cur = (1LL * cur * C(len - 1, a - 1)) % mod;
        cur = (1LL * cur * pw(3, a + b + c - len)) % mod;
        result = (result + cur) % mod;
    }
    cout << result << endl;
    return 0;
}

