        while(M--){
            scanf("%d%d%d",&u,&v,&id);
            AddEdge(u,v,id);
            AddEdge(v,u,id);
        }
        BFS(1,N);
        if(d[N]==INF) d[N]=-1;
        printf("%d\n",d[N]);
    }
    return 0;
}

