ll ans;
const int N = 3e5 + 1;

ll pascalsum[2*N];

int add(int a, int b) {
	return (((a + b) % MOD) + MOD) % MOD;
}

int mul(int a, int b){
	return (ll)a*b%MOD;
}

int pow(int a, int b) {
	int res = 1;
	while(b) {
		if(b&1) res = mul(res, a);
		a = mul(a, a);
		b >>=1;
	}
	return res;
}

int inv(int a) {
	return pow(a, MOD - 2);
}

int fact[N*2];
int invFact[N*2];

int nCr(int n, int r) {
	return mul(fact[n], mul(invFact[r], invFact[n-r]));
}

int main() {
	//#####//
	fact[0]  = invFact[0] = 1;
	FOR(i, 1, N*2) {
		fact[i] = (ll)fact[i-1] * i % MOD;
		invFact[i] = inv(fact[i]);
	}

	int n=ini, M=ini, K=ini;
	pascalsum[0] = 1;
	FOR(i, 1, M + K + 1) {
		pascalsum[i] = mul(2, pascalsum[i-1]);
		if(i >= M+1) pascalsum[i] = add(pascalsum[i], -nCr(i-1, M));
		if(i >= K+1) pascalsum[i] = add(pascalsum[i], -nCr(i-1, i - K - 1));
	}

	FOR(t, n, n+M+K+1) {
		int T = n + M + K;
		ans += (ll)nCr(t-1, n-1) * pow(3, T-t) % MOD * pascalsum[t-n] % MOD;
