        a[i] = fact<modint<M, M - 1>>(n - 1 + i);
    }
    vector<modint<M, M - 1>> b(k + 1);
    for(int j = 0; j <= k; j++) {
        b[j] = fact_inv<modint<M, M - 1>>(j) * (modint<M, M - 1>(3) ^ (k - j));
    }
    reverse(b.begin(), b.end());
    b.resize(a.size());
    a *= b;
    return a;
}
void ex_gcd(ll a, ll b, ll& x, ll& y) {
    if(b == 0) {
        x = 1, y = 0;
    } else {
        ll xx, yy;
        ex_gcd(b, a % b, xx, yy);
        x = yy, y = xx - a / b * yy;
    }
}
const int M1 = 998244353;
const int M2 = 469762049;
int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
