using namespace std;
#define MOD 1000000007

vector<long long> fact_num, fact_den;
vector<long long> pow2, pow3;
map<pair<long long, long long>, long long> dp;

long long bipow(long long n, int m) {
    if (m == 0) return 1;
    if (m % 2 == 0) {
        n = (n * n) % MOD;
        return bipow(n, m / 2) % MOD;
    } else
        return (n * bipow(n, m - 1)) % MOD;
}

void set_table(int N, vector<long long> &fact_num, vector<long long> &fact_den){
    fact_num.resize(N+1, 0);
    fact_den.resize(N+1, 0);
    vector<long long> fact_denpow(N+1, 0);

    fact_num[0] = 1;
    for(int i=1; i<=N; i++) fact_num[i] = (fact_num[i-1] * i) % MOD;

    for(int i=0; i<=N; i++) fact_denpow[i] = bipow(i, MOD-2);
    fact_den[0] = 1;
    for(int i=1; i<=N; i++) fact_den[i] = (fact_den[i-1] * fact_denpow[i]) % MOD;
}

long long comb(const vector<long long> &fact_num, const vector<long long> &fact_den, int m, int n){
    return ((fact_num[m] * fact_den[n]) % MOD * fact_den[m-n]) % MOD;
}

long long func2(long long n, long long k){
    if(k < 0) return 0;

    if(dp.find(make_pair(n, k)) != dp.end())
        return dp[make_pair(n, k)];

    long long tmp = (2 * func2(n-1, k-1))%MOD;
    tmp += comb(fact_num, fact_den, n-1, k);
    tmp %= MOD;
    dp[make_pair(n, k)] = tmp;
    return tmp;
}

long long func(long long L, long long R, long long n){
    long long ret = pow2[n];
    ret += MOD - func2(n, L-1);
    ret %= MOD;
    ret += MOD - func2(n, n-R-1);
    ret %= MOD;
    return ret;
}

int main(){
    long long N, M, K;
    cin >> N >> M >> K;

    set_table(N+M+K, fact_num, fact_den);

    pow2.resize(N+M+K+1, 0);
    pow2[0] = 1;
    for(int i=1; i<=N+M+K; i++)
        pow2[i] = (2*pow2[i-1])%MOD;

    pow3.resize(N+M+K+1, 0);
    pow3[0] = 1;
    for(int i=1; i<=N+M+K; i++)
        pow3[i] = (3*pow3[i-1])%MOD;

    long long ans = 0;
    for(int i=N; i<=N+M+K; i++){
        long long tmp = pow3[N+M+K-i];

        tmp *= comb(fact_num, fact_den, i-1, N-1);
        tmp %= MOD;

        tmp *= func(max(0LL, i-N-K), min(M, i-N), i-N);
        tmp %= MOD;

        ans += tmp;
        ans %= MOD;
    }
    cout << ans << endl;

	return 0;
}

