long long int power(long long int x, long long int n, long long int M) {
	long long int ret = 1;
	long long int by = x;
	while (n) {
		if (n & 1) {
			ret *= by;
			ret %= M;
		}
		by *= by;
		by %= M;
		n >>= 1;
	}
	return ret;
}

int main() {
	ios::sync_with_stdio(false);
	cin.tie(0);

	cin >> N >> M >> K;
	int sum = N + M + K;
	if (M > K)swap(M, K);
	vector<long long int>by(sum + 11, 1);
	vector<long long int>rev(sum + 11, 1);
	for (int i = 2; i <= sum + 10; i++) {
		by[i] = by[i - 1] * i;
		by[i] %= MOD;
		rev[i] = power(by[i], MOD - 2, MOD);
	}
	long long int ans = power(3, M + K, MOD);
	//long long int sum = 1;
	long long int cnt = 1;
	for (int i = N + 1; i <= sum; i++) {
		long long int add = by[i - 1];
		add *= rev[N - 1];
		add %= MOD;
		add *= rev[i - N];
		add %= MOD;
		int l = i - N - 1 - M, r = K;
		cnt *= 2;
		if (l >= 0) {
			long long int minus = by[i - 1 - N];
			minus *= rev[l];
			minus %= MOD;
			minus *= rev[i - 1 - N - l];
			minus %= MOD;
			cnt -= minus;
		}
		if (r <= i - 1 - N) {
			long long int minus = by[i - 1 - N];
			minus *= rev[r];
			minus %= MOD;
			minus *= rev[i - 1 - N - r];
			minus %= MOD;
			cnt -= minus;
		}
		cnt += MOD*2;
		//cout << i << " " << cnt << endl;
		cnt %= MOD;
		add *= cnt;
		add %= MOD;
		add *= power(3, sum - i, MOD);
		add %= MOD;
		ans += add;
		ans %= MOD;
	}
	cout << ans << endl;
}

