      s0=nv1;
    } else {
      lli ns0 = s0+s0-v1+v0;
      ns0%=MOD;
      if(nl1>l1) ns0 += nv1;
      if(nl0>l0) ns0 -= nv0;
      ns0 %= MOD;
      ns0 += MOD;
      ns0 %= MOD;
      s0 = ns0;
    }
    // FORU(j, nl0, nl1) {
    //   s += cnk[i-n][j];
    // }
    lli s = nv0+s0;
    l0 = nl0; l1 = nl1;
    v0 = nv0; v1 = nv1;
    s %= MOD;
    s *= cur0;
    s %= MOD;
    s *= p3[n+m+k-i];
    s %= MOD;
    (ans += s) %= MOD;

    cur0 = cur0+((cur0*invmod(i-n+1))%MOD)*(n-1); cur0 %= MOD; cur0 += MOD; cur0 %= MOD;
  }
  cout << ans << endl;
  return 0;
}

